INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (134977, 'Rifampicin', 'Maraviroc', 'Major', 'Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc may have an increased risk of postural hypotension due to increased maraviroc exposure. Moreover, these patients often have cardiovascular comorbidities that could predispose them to adverse cardiovascular events triggered by postural hypotension. Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers. However, if a potent CYP450 3A4 inhibitor such as itraconazole, ketoconazole, delavirdine, clarithromycin, telithromycin, nefazodone, or any protease inhibitor (except tipranavir plus ritonavir) is also used in combination with the inducer, then maraviroc dosage should be reduced to 150 mg twice daily. Maraviroc is contraindicated for use with potent CYP450 3A4 inducers in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135134/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Paritaprevir, Zanamivir, Velpatasvir, Elbasvir, Famciclovir, Tecovirimat, Pibrentasvir, Rimantadine, Valganciclovir, Telbivudine, Entecavir, More', 1767369485);
