INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (135200, 'Vincristine', 'Teniposide', 'Minor', 'Concurrent administration of vincristine and teniposide may lead to an earlier onset and/or an increased severity of side effects. The mechanism is the inhibition of the metabolism of both drugs by a hepatic cytochrome P-450 isoenzyme in the CYP 3A subfamily. The clinical significance of this interaction has not been established. If vincristine and teniposide are used concomitantly, caution and close observation are advised.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135357/', '', 'Vincristine', 'Teniposide, Irinotecan', 1767369485);
