INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (135690, 'Rapacuronium', 'Methylprednisolone', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants. The mechanism is unknown. In addition, the risk of myopathy may be increased after long-term use of neuromuscular blocking agents and corticosteroids.', 'DDInter', 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy. Dose adjustments of the muscle relaxant may be required. The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Others', 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135847/', '', 'Minocycline, Vitamin A, Isotretinoin, Chloramphenicol, Fluorometholone, Tretinoin, Erythromycin, Dapsone, Azelaic acid, Adapalene, Fluocinolone acetonide, More', '', 1767369485);
