INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (135941, 'Irbesartan', 'Mefenamic acid', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', 'DDInter', 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136098/', '', 'Chondroitin sulfate, Esomeprazole, Glucosamine', 'Sacubitril', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (135942, 'Mefenamic acid', 'Lepirudin', 'Moderate', 'Theoretically, the coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and thrombin inhibitors may potentiate the risk of bleeding. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals.', 'DDInter', 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving thrombin inhibitors. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.', 'Synergism', 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving thrombin inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136099/', '', 'Protein C, Tirofiban, Acenocoumarol, Selexipag', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 1767369485);
