INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (135996, 'Pralatrexate', 'Mefenamic acid', 'Moderate', 'Coadministration with drugs that are subject to substantial renal clearance such as trimethoprim/sulfamethoxazole and nonsteroidal anti-inflammatory drugs (NSAIDs) may result in delayed clearance of pralatrexate. The mechanism is competitive inhibition of renal excretion, as approximately 34% of pralatrexate is eliminated unchanged by the kidney.', 'DDInter', 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs. Complete blood cell counts as well as renal and hepatic function should be closely monitored. Treatment interruption or dose reduction of pralatrexate may be required if toxicities such as mucositis, myelosuppression, or liver enzyme abnormalities develop. Patients should be advised to contact their physician if they develop oral ulcers, painful swallowing, vomiting, diarrhea, and/or signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Excretion', 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136153/', '', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Celecoxib, Valdecoxib', 'Fluorouracil, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Fludarabine, Decitabine, Azacitidine, Capecitabine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (135997, 'Prasugrel', 'Mefenamic acid', 'Major', 'Concomitant use of prasugrel with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, dextran, nonsteroidal anti-inflammatory agents (chronic), or agents that commonly cause thrombocytopenia may increase the risk of bleeding. Prasugrel may be used with aspirin, heparin, or glycoprotein IIb/IIIa inhibitors.', 'DDInter', 'Caution is advised if prasugrel is used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prasugrel is used in combination with other drugs that affect hemostasis.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136154/', '', 'Misoprostol, Chondroitin sulfate, Glucosamine', 'Protein C, Tirofiban, Acenocoumarol, Selexipag', 1767369485);
