INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (136407, 'Meloxicam', 'Lepirudin', 'Moderate', 'Theoretically, the coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and thrombin inhibitors may potentiate the risk of bleeding. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals.', 'DDInter', 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving thrombin inhibitors. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.', 'Synergism', 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving thrombin inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136564/', '', 'Protein C, Acenocoumarol, Selexipag', 'Ropivacaine, Lidocaine, Chloroprocaine, Levobupivacaine, Bupivacaine, Tetracaine, Mepivacaine, Procaine, Prilocaine, Articaine, Etidocaine, More', 1767369485);
