INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (136501, 'Tobramycin', 'Meloxicam', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', 'DDInter', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136658/', '', 'Ganciclovir, Rifamycin, Oxytetracycline, Polymyxin B, Sulfamethizole, Trifluridine, Benzylpenicillin, Ampicillin, Chloramphenicol, Sulfisoxazole, Chlorhexidine, More', 'Ropivacaine, Chloroprocaine, Levobupivacaine, Tetracaine, Bupivacaine, Mepivacaine, Procaine, Prilocaine, Articaine, Etidocaine, Misoprostol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (136502, 'Tofacitinib', 'Meloxicam', 'Moderate', 'Baricitinib and tofacitinib, both Janus kinase (JAK) inhibitors, have been associated with an increased risk of diverticulitis (DV) and gastrointestinal (GI) perforation, particularly in patients with risk factors (e.g., history of diverticulosis or diverticulitis, concomitant use of other agents associated with DV).', 'DDInter', 'Caution is recommended when using baricitinib or tofacitinib in patients with a history of diverticular disease and in patients receiving long-term concomitant treatment with drugs associated with an increased risk of DV and/or GI perforation, such as aspirin, NSAIDs, corticosteroids, and opioids. Patients should be advised to contact their healthcare provider if they experience signs of DV or GI perforation, such as severe abdominal pain, fever, nausea, or vomiting.', 'Synergism', 'Caution is recommended when using baricitinib or tofacitinib in patients with a history of diverticular disease and in patients receiving long-term concomitant treatment with drugs associated with an increased risk of DV and/or GI perforation, such as aspirin, NSAIDs, corticosteroids, and opioids.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136659/', '', 'Ropivacaine, Chloroprocaine, Levobupivacaine, Tetracaine, Bupivacaine, Mepivacaine, Procaine, Prilocaine, Articaine, Etidocaine, Rabeprazole, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Siponimod, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (136503, 'Tolazamide', 'Meloxicam', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', 'DDInter', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral antidiabetic dosage(s) may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their doctor if it occurs. Patients should be observed for loss of glycemic control when these drugs are withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/136660/', '', 'Ropivacaine, Lidocaine, Chloroprocaine, Levobupivacaine, Bupivacaine, Tetracaine, Mepivacaine, Procaine, Prilocaine, Articaine, Etidocaine, More', 'Lixisenatide, Albiglutide, Guar gum, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Troglitazone, Ertugliflozin, Semaglutide, Linagliptin, More', 1767369485);
