INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (136900, 'Ethosuximide', 'Meperidine', 'Moderate', 'The central nervous system and respiratory depressant effects of meperidine may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered, particularly in elderly or debilitated patients.', 'DDInter', 'Caution and dosage adjustments are advisable when meperidine is used in combination with other narcotic analgesics, general anesthetics, phenothiazines, sedative-hypnotics, tranquilizers, tricyclic antidepressants, or other CNS depressants such as alcohol. A lower dosage of meperidine should be considered initially, then titrated carefully according to pain level and clinical response. Meperidine dosage reductions of 25% to 50% have been recommended for patients receiving phenothiazines and other tranquilizers.', 'Synergism', 'Caution and dosage adjustments are advisable when meperidine is used in combination with other narcotic analgesics, general anesthetics, phenothiazines, sedative-hypnotics, tranquilizers, tricyclic antidepressants, or other CNS depressants such as alcohol.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137057/', '', 'Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', '', 1767369485);
