INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (136995, 'Phenytoin', 'Meperidine', 'Moderate', 'Phenytoin may induce the metabolism of meperidine to normeperidine, the latter of which has been associated with adverse effects such as lethargy, agitation, and seizures. The interaction may decrease the therapeutic effects of meperidine while increasing its adverse effects.', 'DDInter', 'Meperidine product labeling recommends that the combination of meperidine and phenytoin be avoided. The same precaution may be applicable to other hydantoins, although clinical data are lacking.', 'Metabolism', 'Meperidine product labeling recommends that the combination of meperidine and phenytoin be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137152/', '', 'Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (136996, 'Pimavanserin', 'Meperidine', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', 'DDInter', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137153/', '', 'Naltrexone, Acetaminophen, Opium, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Amisulpride', 1767369485);
