INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137299, 'Timolol', 'Methylphenobarbital', 'Moderate', 'Coadministration with barbiturates may decrease the plasma concentrations and pharmacologic effects of certain beta-blockers when administered orally. The proposed mechanism is barbiturate induction of hepatic microsomal and first-pass metabolism.', 'DDInter', 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days. Pharmacologic response to beta-blockers should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the beta-blocker dosage adjusted as necessary. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'Metabolism', 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137456/', '', 'Nadolol, Esmolol, Betaxolol, Bisoprolol, Carteolol, Sotalol, Pindolol, Atenolol, Latanoprost, Methazolamide, Neostigmine, More', 'Brivaracetam, Ezogabine, Cenobamate, Ethosuximide, Tiagabine, Ethotoin, Rufinamide, Paramethadione, Perampanel, Ganaxolone, Fosphenytoin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137300, 'Tinidazole', 'Methylphenobarbital', 'Moderate', 'Coadministration with a barbiturate may significantly decrease the plasma concentrations and pharmacologic effects of nitroimidazoles. The proposed mechanism is barbiturate induction of nitroimidazole metabolism via hepatic microsomal enzymes.', 'DDInter', 'Patients treated concomitantly with a barbiturate may require a higher dosage of metronidazole to achieve a cure. Two- to threefold dosage increases are not uncommon. Pharmacologic response to metronidazole should be monitored and the dosage adjusted as necessary. Given its structural similarities to metronidazole, the same precaution may be applicable during therapy with tinidazole, although clinical data are lacking.', 'Metabolism', 'Patients treated concomitantly with a barbiturate may require a higher dosage of metronidazole to achieve a cure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137457/', '', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Ethotoin, Rufinamide, More', 'Tetracycline, Fluconazole, Cefepime, Norfloxacin, Ciprofloxacin, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Levofloxacin, More', 1767369485);
