INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137338, 'Anisindione', 'Mercaptopurine', 'Moderate', 'Limited clinical data suggest that the purine analog antimetabolites, azathioprine and mercaptopurine, may reduce the hypoprothrombinemic response to warfarin and other oral anticoagulants. There have been case reports of patients stabilized on oral anticoagulant therapy whose prothrombin time or INR decreased following addition of the antimetabolites and returned to normal only after their discontinuation or an increase in anticoagulant dosage.', 'DDInter', 'Given the potential for interaction and the narrow therapeutic index of oral anticoagulants, patients should be closely monitored during concomitant therapy with azathioprine or mercaptopurine. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of antimetabolite therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Antagonism', 'Given the potential for interaction and the narrow therapeutic index of oral anticoagulants, patients should be closely monitored during concomitant therapy with azathioprine or mercaptopurine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137495/', '', 'Methotrexate, Pemetrexed, Cladribine, Clofarabine, Trifluridine, Floxuridine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, Decitabine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137339, 'Bacillus calmette-guerin substrain tice live antigen', 'Mercaptopurine', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137496/', '', 'Theophylline, Omalizumab, Tezepelumab, Aminophylline, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, Mepolizumab, More', '', 1767369485);
