INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137356, 'Febuxostat', 'Mercaptopurine', 'Major', 'Coadministration with febuxostat may significantly increase the plasma concentrations of xanthine oxidase substrates such as azathioprine, mercaptopurine, and theophylline. Toxicity may result. The proposed mechanism is inhibition of xanthine oxidase by febuxostat. Although drug interaction studies have not been conducted with febuxostat and xanthine oxidase substrates, the interaction has been reported with allopurinol, another xanthine oxidase inhibitor. Severe bone marrow suppression and other toxicities have been associated with concomitant use of allopurinol and mercaptopurine (6-MP) or azathioprine. Increased theophylline concentrations have also been reported during coadministration with allopurinol at dosages of 600 mg/day or greater.', 'DDInter', 'The use of febuxostat in patients treated with azathioprine, mercaptopurine, or theophylline is considered contraindicated.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137513/', '', 'Fluorouracil, Pemetrexed, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, Decitabine, More', 'Lesinurad, Pegloticase, Sulfinpyrazone, Colchicine, Rasburicase, Probenecid', 1767369485);
