INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137415, 'Pfizer-BioNTech Covid-19 Vaccine', 'Mercaptopurine', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137572/', '', 'Theophylline, Omalizumab, Tezepelumab, Aminophylline, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, Mepolizumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137416, 'Siponimod', 'Mercaptopurine', 'Major', 'Coadministration of siponimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects.', 'DDInter', 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration and for 3 to 4 weeks after the last dose of siponimod. When switching from drugs with prolonged immune effects to siponimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/137573/', '', 'Theophylline, Omalizumab, Tezepelumab, Aminophylline, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, Mepolizumab, More', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', 1767369485);
