INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137947, 'Methazolamide', 'Levacetylmethadol', 'Major', 'Levomethadyl acetate can cause QT interval prolongation and torsades de pointes. Coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in an elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes.', 'DDInter', 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with levomethadyl acetate. A 12-lead ECG should be performed before initiating therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to immediately seek medical attention if they experience palpitations, dizziness, lightheadedness, fainting, or seizures.', 'Synergism', 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138104/', '', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Clindamycin, Trichlormethiazide, Ciclopirox, Probenecid, More', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Methadone, Nalmefene, Nicotine, Buprenorphine', 1767369485);
