INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137972, 'Quinapril', 'Methazolamide', 'Moderate', 'Although they are frequently combined in clinical practice, diuretics and angiotensin converting enzyme (ACE) inhibitors may have additive effects. Coadministration makes hypotension and hypovolemia more likely than does either drug alone. Some ACE inhibitors may attenuate the increase in the urinary excretion of sodium caused by some loop diuretics. Some patients on diuretics, especially those on dialysis or a dietary salt restriction, may experience acute hypotension with lightheadedness and dizziness after receiving the first dose of the ACE inhibitor. In addition, ACE inhibitors may cause renal insufficiency or acute renal failure in patients with sodium depletion or renal artery stenosis.', 'DDInter', 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration. The possibility of first-dose hypotensive effects may be minimized by initiating therapy with small doses of the ACE inhibitor, or either discontinuing the diuretic temporarily or increasing the salt intake approximately one week prior to initiating an ACE inhibitor. Alternatively, the patient may remain under medical supervision for at least two hours after the first dose of the ACE inhibitor, or until blood pressure has stabilized.', 'Synergism', 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138129/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Ciclopirox, Vemurafenib, Darunavir, Tioconazole, More', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Hydrochlorothiazide, Indapamide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (137973, 'Quinidine', 'Methazolamide', 'Moderate', 'Coadministration with drugs that can increase urinary pH such as antacids, carbonic anhydrase inhibitors, or urinary alkalinizers may decrease the urinary excretion of quinidine. The proposed mechanism is increased renal tubular reabsorption due to reduced ionization of quinidine in alkaline urine.', 'DDInter', 'Caution is advised if quinidine is used with agents that can increase urinary pH. Quinidine levels may need to be monitored more closely following addition or discontinuation of these agents, and the quinidine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate quinidine toxicity such as tinnitus, hearing loss, visual disturbances, diarrhea, headache, dizziness, palpitations, syncope, or irregular heartbeats.', 'Excretion', 'Caution is advised if quinidine is used with agents that can increase urinary pH.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138130/', '', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Sulfadiazine, Amphotericin B, Trichlormethiazide, Ciclopirox, Tioconazole, Brinzolamide, Furazolidone, More', 'Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Disopyramide, Mexiletine, Bretylium, Moricizine, Ibutilide', 1767369485);
