INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (138705, 'Metipranolol (ophthalmic)', 'Methysergide', 'Moderate', 'Several case reports have suggested that beta-blockers may potentiate the vasoconstrictive action of ergot alkaloids. The exact mechanism of interaction is unknown, but may involve blockade of beta-2-mediated (i.e., sympathetic) vasodilatation. In addition, beta-1 blockade reduces cardiac output, which can diminish blood flow and exacerbate ergot-induced vasospasm.', 'DDInter', 'Except for increased awareness of the interaction, no specific intervention appears to be necessary. Patients should be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Except for increased awareness of the interaction, no specific intervention appears to be necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138864/', '', 'Frovatriptan, Rimegepant, Eptinezumab, Atogepant, Eletriptan, Rizatriptan, Ubrogepant, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, More', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (138706, 'Metipranolol (ophthalmic)', 'Metocurine', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', 'DDInter', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138865/', '', 'Sulbactam, Oxacillin, Dicloxacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, Nafcillin, More', '', 1767369485);
