INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (138760, 'Amoxapine', 'Metoclopramide', 'Major', 'Coadministration of metoclopramide with phenothiazines, neuroleptics, or other antidopaminergic agents (e.g., tetrabenazine) may increase the frequency and severity of extrapyramidal reactions (i.e., acute dystonic reactions, tardive dyskinesia, akathisia, Parkinson-like symptoms) due to additive antidopaminergic effects.', 'DDInter', 'Due to the potential for increased risk of serious and potentially irreversible extrapyramidal reactions, metoclopramide should not be prescribed in combination with other antidopaminergic agents. In addition, metoclopramide should not be used for longer than 12 weeks except in rare cases where therapeutic benefit is anticipated to outweigh the risk of developing tardive dyskinesia.', 'Synergism', 'Due to the potential for increased risk of serious and potentially irreversible extrapyramidal reactions, metoclopramide should not be prescribed in combination with other antidopaminergic agents.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/138919/', '', 'Oxitriptan, St. John''s Wort, Tryptophan', '', 1767369485);
