INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (139705, 'Quinidine', 'Mexiletine', 'Moderate', 'Quinidine may reduce the hepatic metabolism of mexiletine in extensive debrisoquine metabolizers. The mechanism is inhibition of CYP450 2D6. Serum mexiletine levels and risk of toxicity may be increased. The pharmacologic properties of these drugs may be additive and even though the concurrent use of quinidine and mexiletine has been beneficial in some patients, the risk of proarrhythmic effects may be increased.', 'DDInter', 'The patient should be monitored for clinical, laboratory, and electrocardiographic signs and symptoms of mexiletine toxicity after quinidine has been added to the regimen. Patients should be advised to notify their physician if they experience nausea, vomiting, dizziness, drowsiness, blurry vision, increased irregular heartbeat, numbness or tingling, seizures, or syncope.', 'Metabolism, Synergism', 'The patient should be monitored for clinical, laboratory, and electrocardiographic signs and symptoms of mexiletine toxicity after quinidine has been added to the regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139866/', '', 'Quinidine, Tocainide, Moricizine, Bretylium', 'Propafenone, Tocainide, Procainamide, Flecainide, Disopyramide, Mexiletine, Bretylium, Moricizine, Ibutilide', 1767369485);
