INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (139801, 'Nimodipine', 'Miconazole', 'Moderate', 'Coadministration with azole antifungal agents may increase the plasma concentrations of calcium channel blockers (CCBs), especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most CCBs.', 'DDInter', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with azole antifungal agents. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with azole antifungal agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139962/', '', 'Clevidipine', 'Rifamycin, Amphotericin B, Nystatin, Fidaxomicin, Rifaximin, Paromomycin, Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (139802, 'Nisoldipine', 'Miconazole', 'Moderate', 'Coadministration with miconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by miconazole, which is thought to be a relatively potent inhibitor of the isoenzyme. Although systemic absorption following mucous membrane exposure is limited, the potential for interaction with drugs metabolized by CYP450 3A4 cannot be ruled out.', 'DDInter', 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever miconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if miconazole must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/139963/', '', 'Clevidipine', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, More', 1767369485);
