INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (139985, 'Nefazodone', 'Midazolam', 'Moderate', 'Nefazodone, when administered with alprazolam or triazolam, has been reported to increase their half-life, area under the plasma concentration-time curve, and peak plasma concentrations. Increased sedation and altered psychomotor performance have resulted. The mechanism appears to be inhibition of the CYP450 3A4 enzyme by nefazodone. This interaction may occur with all benzodiazepines that undergo oxidative metabolism, although it has been reported only with alprazolam and triazolam.', 'DDInter', 'Patients receiving this combination should be monitored for excessive and prolonged sedation. It may be necessary to adjust benzodiazepine dosage when nefazodone is started or stopped. Lorazepam, oxazepam, and temazepam undergo conjugative metabolism and may be suitable alternatives.', 'Metabolism', 'Patients receiving this combination should be monitored for excessive and prolonged sedation.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140146/', '', 'Thiopental, Ethchlorvynol, Dichloralphenazone, Nitrazepam', 'Levomilnacipran, Oxitriptan, Vortioxetine, Milnacipran, Tryptophan, Vilazodone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (139986, 'Nelfinavir', 'Midazolam', 'Major', 'Coadministration with protease inhibitors (PIs) may significantly increase the plasma concentrations and pharmacologic effects of orally administered midazolam and triazolam. The mechanism is PI inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of these benzodiazepines. Within the class, ritonavir is the most potent CYP450 3A4 inhibitor, while saquinavir is considered one of the weaker ones.', 'DDInter', 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated. Caution and close clinical monitoring are recommended when administering parenteral midazolam in combination with protease inhibitors. Appropriate medical management should be readily available in case of respiratory depression and/or prolonged sedation. Dosage adjustment for midazolam may be appropriate, especially if more than a single dose of midazolam is administered.', 'Metabolism', 'Given the potential for prolonged and/or increased sedation and respiratory depression associated with excessive benzodiazepine blood levels, concomitant use of oral midazolam or triazolam with protease inhibitors is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140147/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, More', 'Temazepam, Remimazolam, Chloral hydrate, Nitrazepam, Thiopental, Paraldehyde, Propiomazine, Meprobamate, Scopolamine, Dexmedetomidine, Methohexital, More', 1767369485);
