INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (141694, 'Gatifloxacin', 'Nateglinide', 'Major', 'Gatifloxacin may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of various quinolones has been associated with disturbances in blood glucose homeostasis possibly stemming from effects on pancreatic beta cell ATP-sensitive potassium channels that regulate insulin secretion.', 'DDInter', 'The use of gatifloxacin is contraindicated in patients with diabetes mellitus. Other quinolones may be safer alternatives in such patients, although all quinolones should be used with caution. Blood glucose should be closely monitored whenever quinolones are prescribed to patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during quinolone therapy, patients should initiate appropriate remedial therapy immediately, discontinue the antibiotic, and contact their physician.', 'Antagonism', 'The use of gatifloxacin is contraindicated in patients with diabetes mellitus.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141855/', '', 'Guar gum', 'Rifamycin, Kanamycin, Ampicillin, Azithromycin, Acyclovir, Netilmicin, Polymyxin B, Famciclovir, Trifluridine, Tobramycin, Benzylpenicillin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (141695, 'Gemfibrozil', 'Nateglinide', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of nateglinide, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Because the antidiabetic effect of nateglinide is dose- and concentration-dependent, close monitoring for the development of hypoglycemia is recommended during coadministration with CYP450 2C9 inhibitors. Patients should regularly monitor their blood sugar and learn how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitation. The dosage of nateglinide may require adjustment if an interaction is suspected. Likewise, patients should be observed for potential loss of glycemic control following discontinuation of the CYP450 2C9 inhibitor, and the nateglinide dosage adjusted as necessary.', 'Metabolism', 'Because the antidiabetic effect of nateglinide is dose- and concentration-dependent, close monitoring for the development of hypoglycemia is recommended during coadministration with CYP450 2C9 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141856/', '', 'Guar gum, Pramlintide, Alogliptin, Empagliflozin, Dulaglutide, Exenatide, Miglitol, Albiglutide, Ertugliflozin, Semaglutide, Lixisenatide, More', 'Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Cerivastatin, Lomitapide, Colestipol, Bempedoic acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (141696, 'Gemifloxacin', 'Nateglinide', 'Major', 'Quinolone antibiotics may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of quinolones has been associated with disturbances in blood glucose homeostasis possibly stemming from effects on pancreatic beta cell ATP-sensitive potassium channels that regulate insulin secretion. Both hyperglycemia and hypoglycemia have been reported, usually in diabetic patients receiving concomitant treatment with an oral hypoglycemic agent or insulin. Although hyperglycemia is significantly more common and infection itself may be an underlying risk factor, hypoglycemia may cause greater morbidity and mortality.', 'DDInter', 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill. Due to the risk of profound and potentially life-threatening hypoglycemia, particular caution is advised during concomitant use of insulin and insulin secretagogues (e.g., sulfonylureas, meglitinides). Patients should also be apprised of the increased risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately, discontinue the quinolone, and contact their physician. Alternative antibiotics may need to be considered.', 'Antagonism', 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141857/', '', 'Guar gum', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (141697, 'Ginseng', 'Nateglinide', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', 'DDInter', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral antidiabetic dosage(s) may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their doctor if it occurs. Patients should be observed for loss of glycemic control when these drugs are withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141858/', '', 'Simvastatin, Guar gum, Rosuvastatin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (141698, 'Glipizide', 'Nateglinide', 'Moderate', 'Coadministration with nateglinide may increase the plasma concentrations of drugs that are substrates of the CYP450 2C9 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2C9 activity by nateglinide.', 'DDInter', 'Caution is advised if nateglinide must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range. A lower initial dosage, as well as clinical and laboratory monitoring, may be appropriate for some drugs.', 'Metabolism', 'Caution is advised if nateglinide must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141859/', '', 'Glipizide, Acetohexamide, Troglitazone, Tolazamide, Tolbutamide', 'Troglitazone, Miglitol, Nateglinide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (141699, 'Glyburide', 'Nateglinide', 'Moderate', 'Coadministration with nateglinide may increase the plasma concentrations of drugs that are substrates of the CYP450 2C9 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2C9 activity by nateglinide.', 'DDInter', 'Caution is advised if nateglinide must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range. A lower initial dosage, as well as clinical and laboratory monitoring, may be appropriate for some drugs.', 'Metabolism', 'Caution is advised if nateglinide must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141860/', '', 'Acetohexamide, Troglitazone, Tolazamide, Glyburide, Tolbutamide', 'Troglitazone, Miglitol, Nateglinide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (141700, 'Goserelin', 'Nateglinide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141861/', '', 'Bicalutamide', 'Guar gum', 1767369485);
