INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (141801, 'Sucralfate', 'Nateglinide', 'Moderate', 'Due to the carbohydrate content in the excipients, sucralfate oral suspension (but not the tablet) may interfere with the therapeutic effects of insulin and other antidiabetic agents.', 'DDInter', 'Caution is advised when sucralfate oral suspension is prescribed to patients with diabetes. Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of sucralfate treatment, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Others', 'Caution is advised when sucralfate oral suspension is prescribed to patients with diabetes.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/141962/', '', 'Tinidazole, Tetracycline, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Cimetidine, Esomeprazole, Nizatidine, Vonoprazan, Amoxicillin', 'Guar gum', 1767369485);
