INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (142065, 'Torasemide', 'Lactulose', 'Moderate', 'The chronic use or abuse of laxatives may potentiate the pharmacologic effects of diuretics. Laxatives can cause significant losses of fluid and electrolytes, including sodium, potassium, magnesium and zinc, and these effects may be additive to those of diuretics.', 'DDInter', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant use with diuretics, patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Synergism', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142226/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Ciclopirox, Darunavir, Tioconazole, Furazolidone, Diiodohydroxyquinoline, More', 'Alvimopan, Sorbitol, Magnesium sulfate, Methylnaltrexone, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Mannitol, Naldemedine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (142066, 'Triamcinolone (ophthalmic)', 'Lactulose', 'Moderate', 'The overuse or abuse of laxatives can cause significant loss of electrolytes and potentiate the risk of hypokalemia associated with corticosteroid therapy. Corticosteroids promote the retention of sodium and water and the excretion of potassium. Although these effects are primarily associated with mineralocorticoids like fludrocortisone, they may also occur with higher dosages of glucocorticoids or adrenocorticotropic agents, particularly if given systemically for longer than brief periods.', 'DDInter', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant therapy with corticosteroids, particularly if fludrocortisone or large doses of a glucocorticoid or adrenocorticotropic agent is given, patients should be counseled to recognize potential signs and symptoms of hypokalemia such as fatigue, myalgia, and muscle weakness. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Antagonism', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142227/', '', 'Alvimopan, Sodium sulfate, Sorbitol, Methylnaltrexone, Tegaserod, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Mannitol, More', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluocinonide, Fluorometholone, Clobetasol, Beclomethasone dipropionate, Ciclesonide, Mometasone furoate, Mometasone, More', 1767369485);
