INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (142456, 'Pasireotide', 'Nebivolol', 'Moderate', 'The risk of significant bradycardia and atrioventricular (AV) block may be increased during concomitant treatment of pasireotide with other drugs that slow heart rate or AV conduction such as beta-blockers, calcium channel blockers, atazanavir, digitalis, flecainide, lacosamide, mefloquine, moricizine, and propafenone.', 'DDInter', 'Pasireotide has not been studied in patients receiving other bradycardic drugs. Because bradycardia and AV block are recognized risk factors for torsade de pointes arrhythmia, electrocardiographic monitoring is recommended prior to initiation of pasireotide and during treatment. Dose adjustments of beta blockers and calcium channel blockers or correction of electrolyte disturbances may be necessary.', 'Synergism', 'Pasireotide has not been studied in patients receiving other bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/142619/', '', 'Acetylsalicylic acid, Valsartan, Irbesartan, Hydrochlorothiazide, Losartan, Olmesartan, Azilsartan medoxomil, Telmisartan, Candesartan, Sacubitril, Aliskiren, Eprosartan', 'Norethisterone, Nafarelin, Elagolix, Cetrorelix, Estradiol, Ganirelix, Relugolix, Gonadorelin', 1767369485);
