INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143062, 'Irinotecan (liposomal)', 'Nelfinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 is the isoenzyme responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 'Concomitant use of irinotecan with potent CYP450 3A4 inhibitors should generally be avoided. Potent CYP450 3A4 inhibitors should be discontinued for at least one week before initiation of treatment with irinotecan.', 'Metabolism', 'Concomitant use of irinotecan with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143225/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Topotecan, Teniposide', 1767369485);
