INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143070, 'Nevirapine', 'Nelfinavir', 'Moderate', 'Coadministration with nevirapine may significantly decrease the plasma concentrations of most protease inhibitors (PIs) except ritonavir. The mechanism is nevirapine induction of PI metabolism via CYP450 3A4.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if nevirapine is prescribed in combination with PIs, particularly if only one PI is used in the antiretroviral regimen. PI dosages may need to be increased when coadministered with nevirapine, although appropriate dosages with respect to safety and efficacy have not been established.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if nevirapine is prescribed in combination with PIs, particularly if only one PI is used in the antiretroviral regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143233/', '', 'Tenofovir alafenamide, Cabotegravir, Zanamivir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, Telbivudine, More', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, Remdesivir, More', 1767369485);
