INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143225, 'Dicoumarol', 'Neomycin', 'Moderate', 'Limited data suggest that the effect of oral anticoagulants may be increased in patients also receiving oral neomycin. Increases in INR are possible. The exact mechanism is unknown but may involve depletion of certain clotting factors due to suppression of vitamin K-producing intestinal flora and inhibition of vitamin K absorption. Clinical data have been conflicting. Significant changes are not expected in patients with normal dietary intake of vitamin K.', 'DDInter', 'The clinician may consider closer INR and clinical monitoring in patients starting or stopping neomycin therapy while also receiving oral anticoagulants. The dose of the oral anticoagulant may need to be adjusted. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', 'The clinician may consider closer INR and clinical monitoring in patients starting or stopping neomycin therapy while also receiving oral anticoagulants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143388/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Fidaxomicin, Paromomycin, Lidocaine, Chlorhexidine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143226, 'Doxacurium', 'Neomycin', 'Major', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', 'DDInter', 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed. If these agents are used concurrently, vital signs should be closely monitored and drug dosages adjusted accordingly. In addition, ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', 'The use of aminoglycosides should generally be avoided during and immediately after surgery in which neuromuscular blockers have been employed.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143389/', '', 'Rifamycin, Miconazole, Nystatin, Fidaxomicin, Rifaximin, Povidone-iodine, Flurbiprofen, Chlorhexidine, Ibuprofen, Tetracycline, Mupirocin, More', '', 1767369485);
