INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143398, 'Neratinib', 'Nafcillin', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided. According to some authorities, if the CYP450 3A4 inducer cannot be avoided, the dose of neratinib should be increased to 320 mg once daily. The daily dose of neratinib should not exceed 320 mg. The previous dose of neratinib may be resumed following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143561/', '', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, More', 'Futibatinib, Regorafenib, Alpelisib, Lenvatinib, Trametinib, Dabrafenib, Asciminib, Vandetanib, Nintedanib, Afatinib, Dacomitinib, More', 1767369485);
