INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143498, 'Erdafitinib', 'Nevirapine', 'Major', 'Coadministration with moderate inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib. The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', 'DDInter', 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered. If a moderate CYP450 2C9 or 3A4 inducer is coadministered after the initial dose increase period, the dose of erdafitinib may be increased up to 9 mg once daily based on serum phosphate levels and tolerability. When a moderate CYP450 2C9 or 3A4 inducer is discontinued, the current dose of erdafitinib may be continued in the absence of erdafitinib toxicity.', 'Metabolism', 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143661/', '', 'Lamivudine, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Futibatinib, Regorafenib, Lenvatinib, Trametinib, Afatinib, Dacomitinib, Tivozanib, Alectinib, Binimetinib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143499, 'Ergometrine', 'Nevirapine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ergot alkaloids, which are substrates of the isoenzyme.', 'DDInter', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143662/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Misoprostol, Dinoprostone, Oxytocin', 1767369485);
