INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143538, 'Imatinib', 'Nevirapine', 'Moderate', 'Imatinib is a substrate as well as a potent competitive inhibitor of the CYP450 3A4 enzymatic pathway. Theoretically, pharmacokinetic interactions may occur during the coadministration of imatinib with drugs that act as both inducer and substrate of the metabolic pathway. However, data are currently insufficient to predict the potential consequences and clinical significance of such interactions.', 'DDInter', 'During concomitant therapy, the possibility of a diminished therapeutic response to imatinib should be considered. In addition, clinical and laboratory monitoring for potentially increased pharmacologic effects of concomitantly administered medications, particularly those with a narrow therapeutic range, may be appropriate.', 'Metabolism', 'During concomitant therapy, the possibility of a diminished therapeutic response to imatinib should be considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143701/', '', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, Elbasvir, More', 'Futibatinib, Regorafenib, Lenvatinib, Trametinib, Afatinib, Dacomitinib, Tivozanib, Alectinib, Binimetinib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143539, 'Indacaterol', 'Nevirapine', 'Moderate', 'Coadministration with nevirapine may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4. The proposed mechanism is increased clearance due to nevirapine-mediated induction of CYP450 3A4.', 'DDInter', 'Caution is advised if nevirapine is used concomitantly with drugs that are CYP450 3A4 substrates. Dosage adjustments as well as clinical and laboratory monitoring of coadministered drugs should be considered whenever nevirapine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if nevirapine is used concomitantly with drugs that are CYP450 3A4 substrates.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143702/', '', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Etravirine, Dolutegravir, Oseltamivir, More', 'Mometasone furoate, Terbutaline, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Olodaterol, Cromoglicic acid, Ipratropium, Orciprenaline, Pirbuterol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143540, 'Indinavir', 'Nevirapine', 'Moderate', 'Coadministration with nevirapine may significantly decrease the plasma concentrations of most protease inhibitors (PIs) except ritonavir. The mechanism is nevirapine induction of PI metabolism via CYP450 3A4.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if nevirapine is prescribed in combination with PIs, particularly if only one PI is used in the antiretroviral regimen. PI dosages may need to be increased when coadministered with nevirapine, although appropriate dosages with respect to safety and efficacy have not been established.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if nevirapine is prescribed in combination with PIs, particularly if only one PI is used in the antiretroviral regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143703/', '', 'Tenofovir alafenamide, Cabotegravir, Zanamivir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, Telbivudine, More', 'Glecaprevir, Cabotegravir, Zanamivir, Lenacapavir, Valaciclovir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, Remdesivir, Famciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143541, 'Irinotecan (liposomal)', 'Nevirapine', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme may decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4, and induction of this process results in less of the drug available in the plasma for conversion to SN-38 via carboxylesterases..', 'DDInter', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers. Pharmacologic response to irinotecan should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the irinotecan dosage adjusted as necessary.', 'Metabolism', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143704/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Topotecan', 1767369485);
