INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143756, 'Nicardipine', 'Methoxyflurane', 'Moderate', 'Calcium channel blockers may potentiate the cardiac depressant and hypotensive effects of volatile (halogenated) anesthetics, and vice versa, due to their similar pharmacologic action on the cardiovasculature. Serious consequences of this interaction may include cardiovascular collapse, ventricular impairment, and/or conduction disturbances, particularly in patients with underlying cardiac complications. The interaction may be more likely with verapamil and diltiazem due to their dromotropic and chronotropic effects.', 'DDInter', 'Discontinuation of calcium channel blockers prior to surgery is generally not recommended because of the rebound effect in patients with coronary heart disease or arterial hypertension. However, extreme care must be taken when administering halogenated anesthetics to patients under treatment with calcium channel blockers. Hemodynamic and electrocardiographic monitoring are recommended during the operation and postoperatively, and the dosage of one or both drugs adjusted as necessary.', 'Synergism', 'Discontinuation of calcium channel blockers prior to surgery is generally not recommended because of the rebound effect in patients with coronary heart disease or arterial hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143919/', '', 'Clevidipine, Mibefradil', 'Ziconotide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143757, 'Nicardipine', 'Metocurine', 'Moderate', 'Some calcium channel blockers may enhance the neuromuscular blocking properties of nondepolarizing muscle relaxants. The mechanism is not known, but may involve blockage of calcium channels on skeletal muscle. Data are available for verapamil, diltiazem, nicardipine, atracurium, vecuronium, and pancuronium.', 'DDInter', 'Patients should be closely monitored for prolonged neuromuscular blockade. Reduced doses of muscle relaxants may be necessary. Profound neuromuscular blockade may be reversed by neostigmine or edrophonium.', 'Synergism', 'Patients should be closely monitored for prolonged neuromuscular blockade.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143920/', '', 'Clevidipine, Mibefradil, Nisoldipine, Celecoxib', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (143758, 'Nicardipine', 'Nafcillin', 'Moderate', 'Concurrent administration of nafcillin has been reported to result in a large reduction in the plasma concentration of nifedipine. A similar reaction and possible loss of efficacy may be expected if nafcillin were used concurrently with other calcium channel blockers including diltiazem, felodipine, nicardipine, or verapamil. Nafcillin is a potent inducer of CYP450 3A4 enzymes which metabolize these calcium channel blockers.', 'DDInter', 'If these calcium channel blockers must be or are being used with nafcillin, the patient should be monitored closely for change in the efficacy of the calcium channel blocker, when nafcillin is added to or removed from the patient''s drug therapy. Use of a different antibiotic may be appropriate.', 'Metabolism', 'If these calcium channel blockers must be or are being used with nafcillin, the patient should be monitored closely for change in the efficacy of the calcium channel blocker, when nafcillin is added to or removed from the patient''s drug therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/143921/', '', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, More', 'Amlodipine, Nisoldipine, Isradipine, Clevidipine, Levamlodipine, Nimodipine, Celecoxib, Mibefradil', 1767369485);
