INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (144941, 'Telithromycin', 'Nisoldipine', 'Moderate', 'Coadministration with telithromycin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by telithromycin.', 'DDInter', 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever telithromycin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if telithromycin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145104/', '', 'Clevidipine', 'Troleandomycin, Dirithromycin, Azithromycin, Clarithromycin, Erythromycin, Lincomycin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (144942, 'Telotristat ethyl', 'Nisoldipine', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', 'DDInter', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145105/', '', 'Clevidipine, Celecoxib', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (144943, 'Temsirolimus', 'Nisoldipine', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', 'DDInter', 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145106/', '', 'Clevidipine, Nicardipine', 'Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (144944, 'Tetrabenazine', 'Nisoldipine', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145107/', '', 'Celecoxib', 'Dextromethorphan, Valbenazine, Dalfampridine, Riluzole, Inotersen, Amifampridine, Deutetrabenazine, Tafamidis', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (144945, 'Thalidomide', 'Nisoldipine', 'Moderate', 'Thalidomide can cause bradycardia and may have additive effects with other drugs that decrease heart rate. Cases of bradycardia have been associated with thalidomide use, some requiring medical interventions. The clinical significance and underlying etiology of the bradycardia observed with thalidomide treatment have not been established. However, the potential for additive bradycardic effects should be considered when used with other drugs that slow cardiac conduction such as beta-blockers, calcium channel blockers, digitalis, flecainide, moricizine, and propafenone. Non-cardiac drugs that may also cause bradycardia include atazanavir, lacosamide, lithium, mefloquine, succinylcholine, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Because bradycardia is a risk factor for torsade de pointes arrhythmia, concerns also exist when thalidomide is used with drugs that prolong the QT interval or cause torsade de pointes.', 'DDInter', 'Caution is advised when thalidomide is used with drugs that can slow the heart rate or induce torsade de pointes arrhythmia. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat. A dose reduction of thalidomide or discontinuation may be required.', 'Synergism', 'Caution is advised when thalidomide is used with drugs that can slow the heart rate or induce torsade de pointes arrhythmia.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145108/', '', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, Upadacitinib, Apremilast, Inebilizumab, Cladribine, Belumosudil, More', '', 1767369485);
