INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (145150, 'Enoxacin', 'Nizatidine', 'Minor', 'Depending on the route of administration, enoxacin effects may be altered by certain histamine-2 (H2) receptor antagonists. Oral enoxacin effects may be decreased due to reduced gastrointestinal solubility and absorption as a result of H2 antagonist-induced reductions in gastric pH. Reports of this interaction exist for parenteral ranitidine. On the other hand, pharmacologic effects of parenteral enoxacin may be increased by cimetidine. The mechanism is speculated to be competition for active renal secretion. Reports of enoxacin interactivity with other H2 antagonists do not exist, although theoretically, all H2 antagonists may produce a similar effect on oral enoxacin. Close clinical monitoring for altered enoxacin effect is recommended in patients receiving concomitant enoxacin and an H2 antagonist.', 'DDInter', '-', 'Absorption, Excretion', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145313/', '', 'Levofloxacin, Norfloxacin, Nalidixic acid, Gemifloxacin, Lomefloxacin, Grepafloxacin, Moxifloxacin, Cinoxacin, Trovafloxacin, Delafloxacin, Sparfloxacin', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Esomeprazole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (145151, 'Erlotinib', 'Nizatidine', 'Moderate', 'INTERVAL: Concurrent administration of agents that increase gastric pH such as H2-receptor antagonists may decrease the oral bioavailability of erlotinib and reduce its concentrations in plasma. The solubility of erlotinib decreases when the pH is over 5, resulting in reduced absorption.', 'DDInter', 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible. If acid-suppression therapy is required during erlotinib treatment, an H2-receptor antagonist such as ranitidine may be considered. Available pharmacokinetic data suggest that erlotinib should be administered once a day, at least 2 hours before or 10 hours after dosing of the H2-receptor antagonist. Additionally, the lowest effective dosage of the H2-receptor antagonist should be prescribed. It may be appropriate to avoid using cimetidine for long-term acid suppression, as it may inhibit the CYP450 3A4-mediated metabolism of erlotinib and increase the risk of toxicity.', 'Metabolism, Absorption', 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145314/', '', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, More', 'Tinidazole, Levofloxacin, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 1767369485);
