INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (145720, 'Sorafenib', 'Nortriptyline', 'Moderate', 'Sorafenib has the potential to prolong QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if sorafenib is used in combination with cumulative high-dose anthracycline therapy or other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting sorafenib therapy and periodically during treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if sorafenib is used in combination with cumulative high-dose anthracycline therapy or other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145883/', '', 'Dabrafenib, Acalabrutinib, Pexidartinib, Tepotinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Phenelzine, Tranylcypromine, Desvenlafaxine, Nefazodone, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', 1767369485);
