INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (145741, 'Thiopental', 'Nortriptyline', 'Moderate', 'Tricyclic antidepressants may counteract the anticonvulsive effects of barbiturates by lowering the seizure threshold. Barbiturates may decrease the serum levels and effects of tricyclic antidepressants by induction of hepatic metabolism.', 'DDInter', 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control. Dose adjustments may be required. Monitoring for clinical evidence of additive toxicity and for clinical and/or laboratory evidence of reduced antidepressant effect is also advisable.', 'Antagonism', 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145904/', '', 'Vilazodone, Sertraline, Maprotiline, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Milnacipran, Vortioxetine, Trazodone, Levomilnacipran, More', 'Desflurane, Enflurane, Etomidate, Nitrous oxide, Nitrazepam', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (145742, 'Thioridazine', 'Nortriptyline', 'Major', 'Coadministration of thioridazine with another phenothiazine or a tricyclic antidepressant (TCA) may result in elevated plasma concentrations of both drugs. Most phenothiazines and TCAs have been found to undergo metabolism by CYP450 2D6, thus competitive inhibition of the enzyme may occur when more than one of these agents are administered. In addition, these agents individually can cause prolongation of the QT interval, thus concomitant administration may result in elevated risk of ventricular arrhythmias including ventricular tachycardia and torsade de pointes.', 'DDInter', 'The use of thioridazine with drugs that can inhibit CYP450 2D6 and/or prolong the QT interval is considered contraindicated.', 'Metabolism, Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145905/', '', 'Oxitriptan, Vilazodone, Vortioxetine, Tryptophan', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (145743, 'Thiothixene', 'Nortriptyline', 'Moderate', 'Agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive effects when used in combination. Excessive parasympatholytic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome. Peripheral symptoms of intoxication commonly include mydriasis, blurred vision, flushed face, fever, dry skin and mucous membranes, tachycardia, urinary retention, and constipation. Central symptoms may include memory loss, disorientation, incoherence, hallucinations, psychosis, delirium, hyperactivity, twitching or jerking movements, stereotypy, and seizures. Central nervous system-depressant effects may also be additively or synergistically increased when these agents are combined, especially in elderly or debilitated patients.', 'DDInter', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Synergism', 'Caution is advised when agents with anticholinergic properties are combined, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/145906/', '', 'Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone', '', 1767369485);
