INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (145996, 'Copper oxodotreotide Cu-64', 'Octreotide', 'Major', 'INTERVAL: Coadministration of copper Cu 64 dotatate with non-radioactive somatostatin analogs may diminish the diagnostic effects of copper Cu 64 dotatate positron emission tomography (PET) imaging. Somatostatin analogs competitively bind to the same somatostatin receptor as copper Cu 64 dotatate, which may prevent the uptake of copper Cu 64 dotatate into tissues and affect the diagnostic imaging results.', 'DDInter', 'PET imaging with copper Cu 64 dotatate should be performed prior to dosing with somatostatin analogs. For patients receiving short-acting somatostatin analogs, a washout period of 2 days is recommended prior to imaging. For patients receiving long-acting somatostatin analogs, a washout period of 28 days is recommended prior to imaging.', 'Distribution', 'PET imaging with copper Cu 64 dotatate should be performed prior to dosing with somatostatin analogs.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146159/', '', 'Norethisterone, Nafarelin, Elagolix, Cetrorelix, Estradiol, Ganirelix, Relugolix, Gonadorelin', '', 1767369485);
