INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (146056, 'Aminophylline', 'Ofloxacin', 'Moderate', 'Some fluoroquinolones have been shown to decrease the hepatic metabolism of theophyllines. The mechanism is inhibition of CYP450 1A2 hepatic metabolism. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity. This drug combination may increase the risk of seizures, especially in the elderly. Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels during concurrent fluoroquinolone therapy.', 'DDInter', 'Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels. Levofloxacin, sparfloxacin, and lomefloxacin have been reported to cause minor or no changes in theophylline levels and may be considered as alternatives. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeat to their physicians.', 'Metabolism', 'Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146219/', '', 'Gatifloxacin, Levofloxacin, Gemifloxacin, Lomefloxacin, Moxifloxacin, Trovafloxacin, Delafloxacin, Sparfloxacin, Tinidazole, Secnidazole, Cefixime, More', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Bromotheophylline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (146057, 'Amoxapine', 'Ofloxacin', 'Moderate', 'Certain quinolones, including levofloxacin, norfloxacin, and ofloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Although the risk of a serious interaction is probably low, caution is recommended if levofloxacin, norfloxacin, or ofloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Although the risk of a serious interaction is probably low, caution is recommended if levofloxacin, norfloxacin, or ofloxacin is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146220/', '', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', 'Enoxacin, Nalidixic acid, Cinoxacin, Trovafloxacin, Delafloxacin, Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, More', 1767369485);
