INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (146432, 'Probenecid', 'Olanzapine', 'Minor', 'Coadministration with probenecid may increase the plasma concentrations of olanzapine. The mechanism is probenecid inhibition of olanzapine glucuronidation via uridine diphosphoglucuronate-glucuronosyltransferase (UGT1A4). In 12 healthy volunteers, probenecid (500 mg orally twice a day) started one day before olanzapine administration (5 mg single dose) increased the mean peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of olanzapine by 18% and 26%, respectively, compared to administration of olanzapine alone. The clinical significance of this interaction and the effect during long-term administration is unknown.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146595/', '', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Quetiapine, Promazine, Thioridazine, Pimozide, Trifluoperazine, Cariprazine, More', 'Lesinurad, Febuxostat, Pegloticase, Colchicine, Sulfinpyrazone, Rasburicase, Tinidazole, Ascorbic acid, Simeprevir, Lactic acid, Mitapivat, More', 1767369485);
