INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (148139, 'Abarelix', 'Osilodrostat', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148302/', '', 'Anastrozole, Exemestane, Fulvestrant', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (148140, 'Aminoglutethimide', 'Osilodrostat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or 2B6 may decrease the plasma concentrations of osilodrostat, which is partially metabolized by these isoenzymes. According to the product labeling, multiple CYP450 isoenzymes (CYP450 3A4, 2B6, and 2D6) and UDP-glucuronosyltransferases contribute to osilodrostat metabolism, and no single pathway contributes greater than 25% to the total clearance. Some of the known CYP450 3A4 and 2B6 inducers also induce UDP-glucuronosyltransferases.', 'DDInter', 'The potential for diminished pharmacologic effects of osilodrostat should be considered during concomitant use of a CYP450 3A4 and/or 2B6 inducer. An increase in the dosage of osilodrostat may be required. Dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations of their signs and symptoms. A reduction in dosage of osilodrostat may be required if the CYP450 3A4 and/or 2B6 inducer is discontinued during treatment with osilodrostat.', 'Metabolism', 'The potential for diminished pharmacologic effects of osilodrostat should be considered during concomitant use of a CYP450 3A4 and/or 2B6 inducer.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148303/', '', 'Anastrozole, Exemestane, Fulvestrant', 'Ketoconazole', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (148141, 'Amoxapine', 'Osilodrostat', 'Moderate', 'Osilodrostat can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if osilodrostat is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating osilodrostat therapy and periodically thereafter. Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if osilodrostat is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148304/', '', 'Phenelzine, Tranylcypromine, Vilazodone, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Bupropion, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (148142, 'Amprenavir', 'Osilodrostat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osilodrostat, which is partially metabolized by the isoenzyme. Clinically, high plasma levels of osilodrostat may increase the risk of adverse effects such as hypocortisolism (which may lead to life-threatening adrenal insufficiency), QT prolongation (which may increase the risk of ventricular arrhythmias including torsade de pointes and sudden death), and elevated androgen and 11-deoxycorticosterone levels (the latter of which may activate mineralocorticoid receptors and cause hypokalemia, edema, and hypertension).', 'DDInter', 'The manufacturer recommends reducing the dosage of osilodrostat by one-half during concomitant use of a potent CYP450 3A4 inhibitor. Further dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations for signs and symptoms of hypocortisolism such as nausea, vomiting, abdominal pain, loss of appetite, fatigue, dizziness, hypotension, abnormal electrolyte levels, and hypoglycemia. Decrease dosing or temporarily discontinue osilodrostat if patients experience symptoms of hypocortisolism or if urine free cortisol levels fall below the target range or there is a rapid decrease in cortisol levels. Stop osilodrostat and administer exogenous glucocorticoid replacement therapy if patients have symptoms of adrenal insufficiency and serum or plasma cortisol levels are below target range. Osilodrostat therapy may be restarted at a lower dosage when symptoms have resolved and cortisol values are within target range. Additionally, an electrocardiogram and serum electrolyte levels should also be obtained prior to initiating osilodrostat therapy and periodically thereafter.', 'Metabolism', 'The manufacturer recommends reducing the dosage of osilodrostat by one-half during concomitant use of a potent CYP450 3A4 inhibitor.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148305/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', '', 1767369485);
