INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (148198, 'Halofantrine', 'Osilodrostat', 'Major', 'Halofantrine can cause dose-related prolongation of the QT interval at recommended therapeutic doses. QTc interval prolongation and death have been reported during combination use of halofantrine and mefloquine. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of halofantrine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148361/', '', 'Artesunate, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (148199, 'Haloperidol', 'Osilodrostat', 'Major', 'Haloperidol can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Haloperidol treatment alone has been associated with a number of reported cases of torsade de pointes and sudden death. In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with haloperidol. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Caution is recommended if haloperidol is used in combination with other drugs that can prolong the QT interval, particularly when administered intravenously or at higher than recommended dosages.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148362/', '', 'Loxapine, Molindone, Thiothixene', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (148200, 'Halothane', 'Osilodrostat', 'Moderate', 'Osilodrostat can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if osilodrostat is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating osilodrostat therapy and periodically thereafter. Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if osilodrostat is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148363/', '', 'Thiopental, Remifentanil, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Sufentanil, Enflurane, Esketamine, Etomidate', '', 1767369485);
