INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (148494, 'Rifampicin', 'Osimertinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. Reduced therapeutic efficacy of osimertinib should be anticipated.', 'DDInter', 'Concomitant use of osimertinib with potent CYP450 3A4 inducers should generally be avoided. However, if concomitant use is required, some authorities recommend increasing the dose of osimertinib to 160 mg daily during treatment with the potent 3A4 inducer and resumed at 80 mg daily, 3 weeks after discontinuation of the potent 3A4 inducer.', 'Metabolism', 'Concomitant use of osimertinib with potent CYP450 3A4 inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148657/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Alectinib, Trametinib, Dacomitinib', 1767369485);
