INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (150098, 'Venetoclax', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venetoclax, which is a substrate of both the isoenzyme and efflux transporter. Increased venetoclax exposure may potentiate the risk of tumor lysis syndrome, particularly at initiation of therapy and during the dosage ramp-up phase, as well as other adverse effects such as diarrhea, nausea, vomiting, neutropenia, anemia, and thrombocytopenia.', 'DDInter', 'Caution is advised when venetoclax is used with inhibitors of CYP450 3A4. Close clinical and laboratory monitoring for the development of tumor lysis syndrome and myelosuppression is recommended following the addition of a CYP450 3A4, and the venetoclax dosage adjusted if necessary. The dosage may be readjusted 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', 'Caution is advised when venetoclax is used with inhibitors of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150261/', '', 'Acalabrutinib, Dacomitinib, Avapritinib, Selumetinib, Cobimetinib, Zanubrutinib, Axitinib, Sunitinib, Ruxolitinib, Lenvatinib, Erlotinib, More', 'Pertuzumab, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (150099, 'Venlafaxine', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venlafaxine and its metabolite, O-desmethylvenlafaxine (ODV). The possibility of prolonged and/or increased pharmacologic effects of venlafaxine should be considered. Although venlafaxine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of venlafaxine. The interaction may be of greater concern in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent).', 'DDInter', 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150262/', '', 'Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Amoxapine, Imipramine, More', 'Dabrafenib, Dacomitinib, Tepotinib, Bosutinib, Selumetinib, Cobimetinib, Capmatinib, Afatinib, Tivozanib, Brigatinib, Axitinib, More', 1767369485);
