INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (150499, 'Iron', 'Pantoprazole', 'Moderate', 'The profound hypochlorhydria induced by proton pump inhibitors (PPIs) may impair the gastrointestinal absorption of nonheme iron, a process that is dependent on an acidic environment.', 'DDInter', 'Patients with iron deficiency may not respond adequately to oral iron replacement therapy during coadministration of proton pump inhibitors. If an interaction is suspected after ruling out other causes, it may be appropriate to discontinue the proton pump inhibitor or consider administering iron parenterally.', 'Absorption', 'Patients with iron deficiency may not respond adequately to oral iron replacement therapy during coadministration of proton pump inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150662/', '', 'Tinidazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Rifabutin, Nizatidine, Ranitidine, Clarithromycin, Amoxicillin', 'Iron sucrose', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (150500, 'Choline salicylate', 'Pantoprazole', 'Minor', 'Coadministration with proton pump inhibitors may decrease the oral bioavailability of aspirin and other salicylates. Proton pump inhibitors may enhance the release rate of salicylates from enteric-coated formulations due to premature disruption of the coating and intragastric release of the drug secondary to an increase in gastric pH. The clinical significance of this interaction is unknown. Theoretically, it may increase the risk of gastric adverse effects associated with salicylates.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150663/', '', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Rifabutin, Cimetidine, Nizatidine, Ranitidine, More', 'Diflunisal, Ziconotide, Methoxyflurane', 1767369485);
