INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (150659, 'Flecainide', 'Paroxetine', 'Moderate', 'Coadministration with certain selective serotonin reuptake inhibitors (SSRIs) may increase the plasma concentrations of antiarrhythmic agents that are metabolized by CYP450 2D6 such as encainide, flecainide, mexiletine, and propafenone. The mechanism is decreased drug clearance due to inhibition of the isoenzyme by some SSRIs. The interaction has been studied specifically with fluoxetine and propafenone.', 'DDInter', 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with antiarrhythmic agents that are metabolized by CYP450 2D6. Lower initial dosages of the antiarrhythmic agent may be appropriate. Patients who are already stabilized on their antiarrhythmic regimen should be monitored for altered effects on myocardial conduction following addition or discontinuation of SSRI therapy (up to 2 weeks for most SSRIs and 5 weeks for fluoxetine due to its long half-life), and the antiarrhythmic dosage adjusted if necessary. Alternatively, an antidepressant that does not interfere with 2D6 metabolism may be considered, such as citalopram, escitalopram, or venlafaxine.', 'Metabolism', 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with antiarrhythmic agents that are metabolized by CYP450 2D6.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150822/', '', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Nefazodone, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, More', 'Tocainide, Procainamide, Disopyramide, Bretylium, Moricizine, Ibutilide', 1767369485);
