INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (150680, 'Haloperidol', 'Paroxetine', 'Moderate', 'The coadministration with paroxetine may increase the plasma concentrations of certain neuroleptic agents such as phenothiazines and others that are thought to be metabolized by CYP450 2D6. The mechanism is decreased neuroleptic clearance due to inhibition of the isoenzyme by paroxetine. The interaction has been studied with risperidone.', 'DDInter', 'Caution is advised if neuroleptic agents such as phenothiazines, haloperidol, risperidone, or zuclopenthixol must be used concomitantly with paroxetine. Pharmacologic response and plasma neuroleptic levels should be monitored more closely whenever paroxetine is added to or withdrawn from therapy, and the neuroleptic dosage adjusted as necessary. Patients should be monitored for the development of extrapyramidal symptoms (e.g., tremor, shuffling gait, drooling, mask-like face, tongue stiffness, muscle spasms or rigidity, involuntary movements) and changes in mental status.', 'Metabolism', 'Caution is advised if neuroleptic agents such as phenothiazines, haloperidol, risperidone, or zuclopenthixol must be used concomitantly with paroxetine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150843/', '', 'Amisulpride, Pimavanserin', 'Oxitriptan, Vilazodone, Vortioxetine, Tryptophan', 1767369485);
