INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151019, 'Cerivastatin', 'Pazopanib', 'Moderate', 'Concomitant use of pazopanib with simvastatin may increase the risk of ALT elevations, possibly due to additive hepatotoxic effects.', 'DDInter', 'Caution is advised if pazopanib is used in combination with simvastatin. Serum liver transaminases and bilirubin should be measured prior to initiation of pazopanib and regularly during treatment as recommended in the product labeling. If ALT elevations occur, simvastatin should be discontinued and guidelines for pazopanib dosage adjustments should be followed, or alternatives to pazopanib considered. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, light colored stools, and jaundice. Insufficient data are available to assess the risk of pazopanib administered concomitantly with other HMG-CoA reductase inhibitors.', 'Synergism', 'Caution is advised if pazopanib is used in combination with simvastatin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151182/', '', 'Gemfibrozil, Fenofibric acid, Evinacumab, Evolocumab, Dextrothyroxine, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, Niacin, Fenofibrate, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, More', 1767369485);
