INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151499, 'Aminophylline', 'Peginterferon alfa-2a', 'Moderate', 'The coadministration with interferons has been associated with decreases in clearance and increases in plasma concentration and elimination half-life of theophylline. The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes, particularly, CYP450 1A2.', 'DDInter', 'Caution is advised if theophylline or other methylxanthines are administered with an interferon. Pharmacologic response and serum theophylline levels should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their methylxanthine regimen. Patients should be advised to contact their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat.', 'Metabolism', 'Caution is advised if theophylline or other methylxanthines are administered with an interferon.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151662/', '', 'Oprelvekin, Sipuleucel-T, Elapegademase, Sargramostim, Histamine, Aldesleukin, Pegademase, Pegfilgrastim, Filgrastim, Glatiramer', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Pentoxifylline, Bromotheophylline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151500, 'Anisindione', 'Peginterferon alfa-2a', 'Moderate', 'Interferons may increase the plasma concentrations and pharmacologic effects of warfarin and other oral anticoagulants. At least one case of this interaction has been reported in the literature. The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes.', 'DDInter', 'Caution is advised if oral anticoagulants are administered with an interferon. The INR should be monitored closely and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Caution is advised if oral anticoagulants are administered with an interferon.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151663/', '', 'Interferon alfa-n1, Oprelvekin, Sipuleucel-T, Elapegademase, Peginterferon beta-1a, Ropeginterferon alfa-2b, Sargramostim, Histamine, Aldesleukin, Pegademase, Pegfilgrastim, More', '', 1767369485);
