INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151884, 'Samarium (153Sm) lexidronam', 'Pemetrexed', 'Major', 'Theoretical concerns exist that chemotherapeutic agents and other bone marrow depressants may potentiate the myelosuppressive effects of samarium sm 153 lexidronam.', 'DDInter', 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks. Moreover, samarium sm 153 lexidronam should not be given after either of these treatments until there has been time for adequate marrow recovery. Caution and close monitoring of bone marrow function are advisable if coadministration with other myelotoxic agents is required. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, unusual bleeding or bruising, or signs of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152047/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151885, 'Sargramostim', 'Pemetrexed', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', 'DDInter', 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152048/', '', 'Interferon alfa-n1, Oprelvekin, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Peginterferon beta-1a, Interferon alfacon-1, Interferon beta-1a, Histamine, Pegademase, Peginterferon alfa-2b, Glatiramer', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151886, 'Pfizer-BioNTech Covid-19 Vaccine', 'Pemetrexed', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152049/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151887, 'Siponimod', 'Pemetrexed', 'Major', 'Coadministration of siponimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of unintended additive immunosuppressive effects.', 'DDInter', 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration and for 3 to 4 weeks after the last dose of siponimod. When switching from drugs with prolonged immune effects to siponimod, the half-life and mode of action of these drugs must be considered to avoid unintended additive immunosuppressive effects while at the same time minimizing risk of disease reactivation.', 'Synergism', 'The safety and efficacy of siponimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152050/', '', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, More', '', 1767369485);
