INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151918, 'Valganciclovir', 'Pemetrexed', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', 'DDInter', 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152081/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (151919, 'Varicella Zoster Vaccine (Recombinant)', 'Pemetrexed', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152082/', '', '', '', 1767369485);
