INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152016, 'Probenecid', 'Penicillamine', 'Moderate', 'Probenecid may interfere with penicillamine metabolite elimination. The therapeutic effect of penicillamine may be reduced and increased toxicity may occur. In one study with two cystinuric patients given single doses of probenecid and penicillamine, increased cysteine excretion and decreased excretion of penicillamine metabolites occurred.', 'DDInter', 'Concomitant use should be avoided in cystinuric patients.', 'Excretion', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152179/', '', 'Auranofin, Aurothioglucose, Sodium aurothiomalate', 'Lesinurad, Febuxostat, Pegloticase, Colchicine, Sulfinpyrazone, Rasburicase, Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, More', 1767369485);
